sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},
    isDatePast: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date
        var rightnow = new GlideDateTime();
        var testdate = new GlideDateTime(strDate);

        // If the testdate is before rightnow, return true, else return false
        if (testdate.before(rightnow)) {
            return true;
        } else {
            return false;
        }
    },

	// If the tesdate is equals to this day, return true -> a.k.a don't submit form
    isDatePresent: function(strDate) {
        var rightnow = new GlideDateTime();
        var testdate = new GlideDateTime(strDate);
		//Create var as per Business Rule without script include -> Use getLocalDate and reference the new var to existing gdt var in function
        var today = rightnow.getLocalDate();
        var whentoday = testdate.getLocalDate();

        if (today.compareTo(whentoday) == 0) {
            return true;
        } else {
            return false;
        }
    },

    type: 'NeedItUtils'
};
